package operators;

public class Assignment_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	
			int x=4,y=5;
			System.out.println(x+=y);//x=x+y//x=9
			System.out.println(x-=y);//x=x-y//4

			System.out.println(x*=y);//x=x*y//20

			System.out.println(x/=y);//x=x/y//20/5=4

	}

}
