package operators;

public class Relatioal_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	
				int x=4,y=3;
				System.out.println(x<y);//4<3
				System.out.println(x>y);//4>3
				System.out.println(x<=y);
				System.out.println(x>=y);
				System.out.println(x==y);

			}



}
